jQuery(document).ready(function(jQuery){jQuery(".ninja-forms-form input").bind("keypress",function(e){if(e.keyCode==13){var type=jQuery(this).attr("type");if(type!="textarea"){return false;}}});jQuery("div.label-inside input[type=text]").focus(function(){var label=jQuery("#"+this.id+"_label_hidden").val();if(this.value==label){this.value='';}});jQuery("div.label-inside input[type=text]").blur(function(){var label=jQuery("#"+this.id+"_label_hidden").val();if(this.value==''){this.value=label;}});jQuery(".ninja-forms-mask").each(function(){var mask=this.title;jQuery(this).mask(mask);});jQuery(".ninja-forms-date").mask('99/99/9999');jQuery(".ninja-forms-datepicker").datepicker({dateFormat:ninja_forms_settings.date_format,});jQuery(".ninja-forms-currency").autoNumeric({aSign:ninja_forms_settings.currency_symbol});jQuery(".ninja-forms-help-text").qtip({style:{classes:'qtip-shadow qtip-dark'}});});function ninja_forms_html_decode(value){if(value){var decoded=jQuery('<div />').html(value).text();decoded=jQuery('<div />').html(decoded).text();return decoded;}else{return'';}}function ninja_forms_toggle_login_register(form_type,form_id){var el_id='ninja_forms_form_'+form_id+'_'+form_type+'_form';if(form_type=='login'){var opp_id='ninja_forms_form_'+form_id+'_register_form';}else{var opp_id='ninja_forms_form_'+form_id+'_login_form';}var ele=document.getElementById(el_id);var opp_ele=document.getElementById(opp_id);if(ele.style.display=="block"){ele.style.display="none";}else{ele.style.display="block";opp_ele.style.display="none";}}function ninja_forms_get_form_id(element){var form_id=jQuery(element).closest('form').prop("id");if(form_id==''){form_id=jQuery("#_form_id").val();}return form_id;}